package leetcode

import "testing"

func Test_longestPalindrome(t *testing.T) {
	type args struct {
		s string
	}
	tests := []struct {
		name string
		args args
		want string
	}{
		{"", args{s: "a"}, "a"},
		{"", args{s: "babad"}, "bab"},
		{"", args{s: "cbbd"}, "bb"},
		{"", args{s: "aa"}, "aa"},
		{"", args{s: "ab"}, "a"},
		{"", args{s: ""}, "lbabl"},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := longestPalindrome(tt.args.s); got != tt.want {
				t.Errorf("longestPalindrome() = %v, want %v", got, tt.want)
			}
		})
	}
}
